const API_URL = 'http://www.omdbi.com/?i=tt3896198&apikey=2f7a6f39'

const App = () => {
  const searchMovies = async(title) => {
    const response = await fetch(`${API_URL}&s=${title}`);
    const data = await response.json();
    console.log(data);
  }
  useEffect(() => {
    searchMovies('Batman')
  }, []); // empty array calls the useEffect only at start

  return (

    <h1>Hello</h1>
);
};

export default App;
